import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

function Chart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/api/sensors")
      .then((res) => res.json())
      .then((json) => {
        // فرمت‌دهی به دیتاست برای نمودار
        const cleaned = json.map((item) => ({
          timestamp: new Date(item.timestamp).toLocaleString("de-DE", {
            day: "2-digit",
            month: "2-digit",
            hour: "2-digit",
            minute: "2-digit",
          }),
          Active_Power: item.Active_Power,
        }));
        setData(cleaned);
      })
      .catch((err) => console.error("خطا در گرفتن دیتا:", err));
  }, []);

  return (
    <div style={{ width: "100%", height: "400px" }}>
      <h2>نمودار Active Power در زمان</h2>
      <ResponsiveContainer>
        <LineChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis dataKey="Active_Power" />
          <Tooltip />
          <Line
            type="monotone"
            dataKey="Active_Power"
            stroke="#8884d8"
            strokeWidth={2}
            dot={false}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default Chart;
